package com.bzkj.activity;import org.mybatis.spring.annotation.MapperScan;import org.springframework.boot.SpringApplication;import org.springframework.boot.autoconfigure.SpringBootApplication;import org.springframework.boot.builder.SpringApplicationBuilder;import org.springframework.boot.web.support.SpringBootServletInitializer;import org.springframework.context.annotation.ComponentScan;/** * @description: * @author: jensen * @date: created by 2019/9/11 2:37 */@SpringBootApplication@MapperScan(basePackages = {"com.bzkj.activity.dao"})@ComponentScan(basePackages = {"com.bzkj.activity.service"})public class ActivityProviderApplication extends SpringBootServletInitializer {    @Override    protected SpringApplicationBuilder configure(SpringApplicationBuilder builder) {        return builder.sources(ActivityProviderApplication.class);    }    public static void main(String[] args) {        SpringApplication.run(ActivityProviderApplication.class, args);    }}