package com.bzkj.controller.activity.draw;import com.bzkj.dto.UserDrawInfo;import com.bzkj.req.DrawReq;import com.bzkj.rsp.Result;import io.swagger.annotations.Api;import io.swagger.annotations.ApiImplicitParam;import io.swagger.annotations.ApiImplicitParams;import io.swagger.annotations.ApiOperation;import org.springframework.web.bind.annotation.GetMapping;/** * @description: * @author: jensen * @date: created by 2019/9/16 11:42 */@Api(tags = "Activity")public interface DrawActivityController {    @ApiOperation("抽奖次数")    @GetMapping("/api/v1/activity/count")    @ApiImplicitParams({            @ApiImplicitParam(paramType = "query", required = true, name = "loginToken", value = "登录令牌"),            @ApiImplicitParam(paramType = "query", required = true, name = "activityId", value = "活动id") })    Result<UserDrawInfo> count(DrawReq drawReq);}