package com.bzkj.controller.activity.login;import io.swagger.annotations.Api;import io.swagger.annotations.ApiImplicitParam;import io.swagger.annotations.ApiImplicitParams;import io.swagger.annotations.ApiOperation;import org.springframework.web.bind.annotation.PostMapping;import javax.xml.transform.Result;import java.util.Map;/** * @description: * @author: jensen * @date: created by 2019/9/17 0:02 */@Api("Activity-Login")public interface LoginController {    @ApiOperation("活动登录")    @PostMapping("/api/v1/activity/login")    @ApiImplicitParams({            @ApiImplicitParam(paramType="query",name="loginParams",value="登录参数",required=true)    })    public Result login(String loginParams);    @ApiOperation("测试活动登录")    @PostMapping("/api/v1/activity/test/login")    @ApiImplicitParams({            @ApiImplicitParam(paramType="query",name="mobile",value="手机号",required=true),            @ApiImplicitParam(paramType="query",name="activityId",value="活动ID",required=true),            @ApiImplicitParam(paramType="query",name="channelId",value="渠道ID",required=true),    })    public Result testLogin(String mobile, String activityId, String channelId);}