package com.bzkj.controller.cms;import com.bzkj.entity.user.SysUser;import com.bzkj.rsp.Result;import io.swagger.annotations.Api;import io.swagger.annotations.ApiImplicitParam;import io.swagger.annotations.ApiImplicitParams;import io.swagger.annotations.ApiOperation;import org.springframework.security.access.prepost.PreAuthorize;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.PostMapping;/** * @description: * @author: jensen * @date: created by 2019/12/14 3:30 */@Api(tags = "User")public interface UserController {    @ApiOperation("用户登录")    @PostMapping("/api/login")    @ApiImplicitParams({            @ApiImplicitParam(paramType = "query", required = true, name = "username", value = "活动id"),            @ApiImplicitParam(paramType = "query", required = true, name = "password", value = "活动id")    })    Result login(String username, String password);    @PreAuthorize("hasAnyAuthority('sys:user:query','sys:user:add')")    @ApiOperation(value = "当前登录用户")    @GetMapping("/api/v1/current")    Result<SysUser> currentUser();}