package com.bzkj.controller.redis;import com.bzkj.annotation.Login;import com.bzkj.rsp.Result;import io.swagger.annotations.Api;import io.swagger.annotations.ApiImplicitParam;import io.swagger.annotations.ApiImplicitParams;import io.swagger.annotations.ApiOperation;import org.springframework.web.bind.annotation.GetMapping;/** * @description: * @author: jensen * @date: created by 2019/9/11 2:05 */@Api(tags = "Redis")public interface RedisController {    @ApiOperation("存储")    @GetMapping("/api/v1/set")    @ApiImplicitParams({            @ApiImplicitParam(paramType = "query", required = true, name = "key", value = "活动id"),            @ApiImplicitParam(paramType = "query", required = true, name = "val", value = "活动id")    })    Result set(String key, String val);    @ApiOperation("获取")    @GetMapping("/api/v1/get")    @ApiImplicitParams({            @ApiImplicitParam(paramType = "query", required = true, name = "key", value = "活动id")    })    @Login    Result get(String key);}