package com.bzkj.handle;import com.bzkj.exception.CommonBizException;import com.bzkj.exception.ExpCodeEnum;import com.bzkj.rsp.Result;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import org.springframework.web.HttpRequestMethodNotSupportedException;import org.springframework.web.bind.annotation.ControllerAdvice;import org.springframework.web.bind.annotation.ExceptionHandler;import org.springframework.web.bind.annotation.ResponseBody;/** * @description: REST接口的通用异常处理 * @author: jensen * @date: created by 2019/9/11 16:31 */@ControllerAdvice@ResponseBodypublic class ExceptionHandle {    private final Logger logger = LoggerFactory.getLogger(this.getClass());    /**     * 业务异常处理     *     * @param exception     * @param <T>     * @return     */    @ExceptionHandler(CommonBizException.class)    public <T> Result<T> exceptionHandler(CommonBizException exception) {        return Result.newFailureResult(exception);    }    /**     * 请求方法不正确     *     * @param exception     * @return     */    @ExceptionHandler(HttpRequestMethodNotSupportedException.class)    public Result HttpRequestMethodNotSupportedException(HttpRequestMethodNotSupportedException exception) {        throw new CommonBizException(ExpCodeEnum.HTTP_REQ_METHOD_ERROR);    }    /**     * 系统异常处理     *     * @param exception     * @return     */    @ExceptionHandler(Exception.class)    public <T> Result<T> sysExpHandler(Exception exception) {        logger.error("系统异常 ", exception);        return Result.newFailureResult();    }}