package com.bzkj.dto;import org.apache.commons.lang3.StringUtils;import java.text.DateFormat;import java.text.SimpleDateFormat;import java.util.Calendar;import java.util.Date;/** * @author HUNTER * */public class DrawDateUtil {    private static DateFormat DF = new SimpleDateFormat("yyyyMMdd");    private static DateFormat DFY = new SimpleDateFormat("yyyy");    private static DateFormat DFM = new SimpleDateFormat("yyyyMM");    /**     * 根据指定类型及日期返回响应的唯一key值（用于控制活动次数限制）     *     * @param type （0不限制时默认无、1每日、2每周、3每月、4活动期间）     * @param date (指定时间 ，为空时 默认取系统时间)     * @return     */    public static String getKeyDate(int type, Date date) {        String key = "";        if (date == null) {            date = new Date();        }        switch (type) {            case 0:                key = "";                break;            case 1:                key = type + DF.format(date);                break;            case 2:                Calendar calendar = Calendar.getInstance();                calendar.setTime(date);                key = type + DFY.format(date) + StringUtils.leftPad(calendar.get(Calendar.WEEK_OF_YEAR) + "", 2, '0');                break;            case 3:                key = type + DFM.format(date);                break;            default:                key = "";        }        return StringUtils.rightPad(key, 9, '0');    }        public static String getCleanKeyDate(int type, Date date) {    	return "CLEAN_" + getKeyDate(type, date);    }}