package com.bzkj.dto;import java.io.Serializable;/** * @description: 抽奖专用实体 * @author: jensen * @date: created by 2019/8/29 16:24 */public class GiftDto implements Serializable {    /**     * 奖品索引     */    private int index;    /**     * 权益id     */    private String rightId;    /**     * 奖品名称     */    private String rightName;        private String startTime;        private String endTime;    /**     * 奖品权重     */    private double probability;    /**     * 未中奖     */    public GiftDto() {        this.index = -1;        this.rightId = "-1";        this.rightName = "未中奖";        this.probability = -1d;    }    public GiftDto(int index, String rightId, String rightName, double probability) {        this.index = index;        this.rightId = rightId;        this.rightName = rightName;        this.probability = probability;    }    public int getIndex() {        return index;    }    public void setIndex(int index) {        this.index = index;    }        public String getRightId() {		return rightId;	}	public void setRightId(String rightId) {		this.rightId = rightId;	}	public String getRightName() {		return rightName;	}	public void setRightName(String rightName) {		this.rightName = rightName;	}	public double getProbability() {        return probability;    }    public void setProbability(double probability) {        this.probability = probability;    }    public String getStartTime() {		return startTime;	}	public void setStartTime(String startTime) {		this.startTime = startTime;	}	public String getEndTime() {		return endTime;	}	public void setEndTime(String endTime) {		this.endTime = endTime;	}	@Override    public String toString() {        return "Gift [index=" + index + ", rightId=" + rightId + ", rightName=" + rightName + ", probability=" + probability + "]";    }}