package com.bzkj.dto;import com.bzkj.utils.DateUtil;import java.io.Serializable;/** * @description: 跑马灯记录表 * @author: jensen * @date: created by 2019/9/2 11:18 */public class HorseReceRecordDto implements Serializable {    private String mobile;    /**     * 中奖时间     */    private String giftTime;    /**     * 奖品名称     */    private String name;    public String getMobile() {        return mobile;    }    public void setMobile(String mobile) {        this.mobile = mobile;    }    public String getGiftTime() {        return giftTime;    }    public void setGiftTime(String giftTime) {        this.giftTime = giftTime;    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public HorseReceRecordDto(String mobile, String giftTime, String name) {        this.mobile = mobile;        this.giftTime = giftTime;        this.name = name;    }    /**     * 转换前端str     * @return     */    public String build(){        String currentDate = DateUtil.getCurrentDate();        long l = DateUtil.timeDifferenceValue(this.giftTime, currentDate) / 1000 ;        String str = "";        l = l <=0 ? 1: l;        if (l < 60) {            str = l+"秒种前 恭喜手机尾号 "+this.mobile.substring(7,mobile.length())+"的用户抽中 "+this.name;        } else if (60 <=l && l< 60*60) {            str = l/60+"分种前 恭喜手机尾号 "+this.mobile.substring(7,mobile.length())+"的用户抽中 "+this.name;        } else if (60*60 < l && l<60*60*24) {            str = l/(60*60)+"小时前 恭喜手机尾号 "+this.mobile.substring(7,mobile.length())+"的用户抽中 "+this.name;        } else {            str = l/(60*60*24)+"天前 恭喜手机尾号 "+this.mobile.substring(7,mobile.length())+"的用户抽中 "+this.name;        }        return str;    }}