package com.bzkj.enumeration.activity;/** * @description: 活动状态 * @author: jensen * @date: created by 2019/8/28 11:04 */public enum ActivityStatusEnum {    ACTIVITY_DISABLE(0, "活动不可用"),    ACTIVITY_GOING(1, "活动进行中"),    ACTIVITY_SUSPEND(2, "活动已暂停"),    ACTIVITY_END(3, "活动已结束");    private Integer status;    private String desc;    ActivityStatusEnum(Integer status, String desc) {        this.status = status;        this.desc = desc;    }    public Integer getStatus() {        return status;    }    public String getDesc() {        return desc;    }}