package com.bzkj.enumeration.cache;/** * @description: * @author: jensen * @date: created by 2019/9/16 13:35 */public class CacheKey {    private String cacheModel;    private String cacheKey;    private String dataKey;    public CacheKey(String cacheModel,String cacheKey, String dataKey) {        this.cacheModel = cacheModel;        this.cacheKey = cacheKey;        this.dataKey = dataKey;    }    public String getCacheKey() {        return cacheKey;    }    public void setCacheKey(String cacheKey) {        this.cacheKey = cacheKey;    }    public String getDataKey() {        return dataKey;    }    public void setDataKey(String dataKey) {        this.dataKey = dataKey;    }    public String getCacheModel() {        return cacheModel;    }    public void setCacheModel(String cacheModel) {        this.cacheModel = cacheModel;    }    public String getKey() {        StringBuilder buffer = new StringBuilder();        buffer.append(cacheModel);        buffer.append(cacheKey);        buffer.append(dataKey);        return buffer.toString();    }}