package com.bzkj.enumeration.cache;/** * @description: 缓存枚举 * @author: jensen * @date: created by 2019/9/16 13:30 */public enum CacheModelEnum {    USER_INFO("USER","USER_INFO","用户信息");    private String cacheModel;    private String cacheKey;    private String cacheDesc;    private CacheModelEnum(String cacheModel, String cacheKey, String cacheDesc) {        this.cacheModel = cacheModel;        this.cacheKey = cacheKey;        this.cacheDesc = cacheDesc;    }    public String getCacheModel() {        return cacheModel;    }    public String getCacheKey() {        return cacheKey;    }    public String getCacheDesc() {        return cacheDesc;    }    public String getCacheKey(String dataKey) {        return new CacheKey(cacheModel,cacheKey,dataKey).getKey();    }}