package com.bzkj.facade.redis;import java.util.Date;import java.util.List;import java.util.Set;import java.util.concurrent.TimeUnit;/** * @description: Redis服务接口 * @author: jensen * @date: created by 2019/9/11 1:19 */public interface RedisService {    public Object get(String key);    public List<Object> getAll(String key);    public Object get(String key, String field);    public void put(String key, Object value, long time, TimeUnit unit);    public void put(String key, Object value);    public Boolean hasKey(String key, String field);    public void put(String key, String field, Object value);    public void delete(String key);    public void delete(String key, Object... field);    public Set<String> keys(String pattern);    public long size(String pattern);    public boolean contain(String key);    public long lpush(String key, Object value);    public Long lPushAll(String key, List list);    public Object rpop(String key);    public Object lpop(String key);    public long remove(String key, Long count, Object value);    public long increment(String key, String field, Long delta);    public List<Object> rang(String key, Long start, Long end);    public long listsize(String key);    public double incrementD(String key, String field, Double delta);    public long sadd(String key, Object values);    public Set<Object> smember(String key);    public boolean expire(String key, Date date);    public boolean expire(String key, long date, TimeUnit unit);    public void atomicSet(String key, long value);    public long atomicGet(String key);    public long incr(String key);    public long decr(String key);}