package com.bzkj.runtime.kafka.consumer;import org.apache.kafka.clients.consumer.ConsumerRecord;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import org.springframework.kafka.annotation.KafkaListener;import org.springframework.stereotype.Component;import java.util.Optional;/** * @description: 消费者 * @author: jensen * @date: created by 2019/9/15 13:21 */@Componentpublic class Consumer {    private Logger logger = LoggerFactory.getLogger("runtimeLogger");    @KafkaListener(topics = {"topic_one"})    public void receiveMessage(ConsumerRecord<?, ?> record) {        Optional<?> kafkaMessage = Optional.ofNullable(record.value());        if (kafkaMessage.isPresent()) {            Object message = kafkaMessage.get();            logger.info("----------------- record =" + record);            logger.info("------------------ message =" + message);        }    }}