package com.bzkj.runtime.kafka.service;import com.alibaba.dubbo.config.annotation.Service;import com.bzkj.facade.runtime.KafkaService;import com.bzkj.rsp.Result;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.kafka.core.KafkaTemplate;/** * @description: kafka 服务 * @author: jensen * @date: created by 2019/9/15 13:27 */@Service(interfaceClass = KafkaService.class)@org.springframework.stereotype.Servicepublic class KafkaServiceImpl implements KafkaService {    @Autowired    private KafkaTemplate<String, Object> kafkaTemplate;    @Override    public Result send(String msg) {        kafkaTemplate.send("topic_one", msg);        return Result.newSuccessResult();    }}